import {Component, OnInit} from '@angular/core';
import {ShopsService} from 'src/core/services/shops.service';
import {UserService} from 'src/core/services/user.service';
import {ActivatedRoute} from '@angular/router';
import {ShopDetailsService} from './shop-details.service';
import {GeocodeService} from 'src/core/services/geocode.service';

declare var jQuery: any;

@Component({
  selector: 'shop-details-page',
  templateUrl: './shop-details.component.html',
  styleUrls: ['./shop-details.component.css'],
  providers: [ShopDetailsService],
})
export class ShopDetailsComponent implements OnInit {
  routedShop = null;
  currentDistanceFromShop: number;
  addressObj = null;

  selectedLocation: any;
  map: google.maps.Map;
  shopObject: any;

  selectedRestaurant: any = {
    label: 'selected-restaurant',
    value: [],
  };
  similarRestaurants: any = {
    label: 'similar-restaurants',
    value: [],
  };
  loading: boolean = false;
  slideConfig = {
    slidesToShow: 3,
    infinite: false,
  };
  private sub: any;
  gallery = [
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
  ];
  constructor(
    private shopService: ShopsService,
    public userService: UserService,
    private route: ActivatedRoute,
    private shopsDetailsServ: ShopDetailsService,
    private geoCodeServ: GeocodeService
  ) {}
  ngOnInit() {
    this.sub = this.route.params.subscribe(params => {
      this.loading = true;
      this.getShopByIdAndNearbyRestaurantsByBranchId(+params['id']);
    });
    this.selectedRestaurant.value = [
      {imageUrl: 'https://bit.ly/2TTXLPB'},
      {imageUrl: 'https://bit.ly/2NdzVMi'},
      {imageUrl: 'https://bit.ly/2TVxa4u'},
      {imageUrl: 'https://bit.ly/2TTXLPB'},
      {imageUrl: 'https://bit.ly/2NdzVMi'},
      {imageUrl: 'https://bit.ly/2TVxa4u'},
    ];
    this.initializeSlick(this.selectedRestaurant.label);
  }
  ngOnDestroy() {
    this.sub.unsubscribe();
  }
  setMap(latlng) {
    this.selectedLocation = latlng;
    var map = new google.maps.Map(document.getElementById('map'), {
      zoom: 12,
      center: this.selectedLocation,
      mapTypeId: google.maps.MapTypeId.ROADMAP,
      disableDefaultUI: true,
    });
    var marker = new google.maps.Marker({
      position: this.selectedLocation,
      map: map,
    });
    marker.setMap(map);
  }
  openMapsInNewTab(e) {
    e.stopPropagation();
    window.open(
      'https://www.google.com/maps/search/?api=1&query=' +
        this.selectedLocation.lat +
        ',' +
        this.selectedLocation.lng,
      '_blank'
    );
  }
  initializeSlick(str: string) {
    let self = this;
    jQuery(document).ready(function() {
      jQuery(`.${str}`).slick(self.shopService.getSlideConfiguration());
    });
  }
  getShopByIdAndNearbyRestaurantsByBranchId(shopId) {
    const userLogin = {
      email: this.userService.getUserFromLocalStorage().email,
      password: this.userService.getUserFromLocalStorage().password,
    };

    this.shopService.searchShopById(shopId, userLogin).subscribe(shop => {
      this.shopService
        .searchSimilarRestaurantsByBranchId(shop.branchId, userLogin)
        .subscribe(shops => {
          this.loading = false;
          // Need to enable this only for testing 'nearby restaurants'
          // this.similarRestaurants.value = shops;
          this.similarRestaurants.value = shops.filter(
            shop => shop.id !== shopId
          );
          this.initializeSlick(this.similarRestaurants.label);
          this.routedShop = shop;
          this.prepareUI();
        });
    });
  }
  copyURLToClipboard() {
    const str = (window as any).location.href;
    this.shopsDetailsServ.copyToClipboard(str);
  }
  prepareUI() {
    this.setMap({
      lat: this.routedShop.latitude,
      lng: this.routedShop.longitude,
    });
    this.geoCodeServ.getLatitudeLongitude().then(currentCoordinates => {
      const shopCoordinates = {
        latitude: this.routedShop.latitude,
        longitude: this.routedShop.longitude,
      };
      this.currentDistanceFromShop = Number(
        this.shopsDetailsServ
          .getDistanceFromLatLonInKm(currentCoordinates, shopCoordinates)
          .toFixed(2)
      );
    });
    if (this.isJson(this.routedShop.address)) {
      this.addressObj = JSON.parse(this.routedShop.address);
      this.addressObj['isJSON'] = true;
    } else {
      this.addressObj = {};
      this.addressObj['address'] = this.routedShop.address;
      this.addressObj['isJSON'] = false;
    }
  }
  isJson(str) {
    try {
      JSON.parse(str);
    } catch (e) {
      return false;
    }
    return true;
  }
}
